import type { App } from "vue"
import hCrud from "./hCrud/index.vue"
import hForm from "./hForm/index.vue"
import hExport from "./hExport/index.vue"
import hUpload from "./hUpload/index.vue"
import hPaging from "./hPaging/index.vue"
import HPreview from "./hPreview/index.vue"

declare module "@vue/runtime-core" {
  export interface GlobalComponents {
    hCrud: typeof hCrud
    hForm: typeof hForm
    hExport: typeof hExport
    hUpload: typeof hUpload
    hPaging: typeof hPaging
    HPreview: typeof HPreview
  }
}

const components = [hCrud, hForm, hExport, hUpload, hPaging, HPreview]

const install = (app: App) => {
  for (const component of components) {
    app.component(component.name, component)
  }
}

export { install }
export default install
